import {TTable} from "./types";
import {TableRow} from "./TableRow";
import './table.css'
import {TableCellHead} from "./TableCellHead";

export const Table = ({headers, data, sort}: TTable) => {
  return (
    <table style={{width: '100%'}} className="table">
      <TableCellHead headers={headers} sort={sort}/>
      <tbody className="table-row">
        {data.map((dataItem) => {
          return <TableRow
            key={dataItem.name}
            option={dataItem}
            data={data}
          />
        })}
      </tbody>
    </table>
  )
}
