import {useState} from "react";
import {TTableRow} from "./types";
import {TUser} from "../../types/types";
import './table.row.css'
// @ts-ignore
import {ReactComponent as Plus} from '../../assets/svg/plus.svg'

export const TableRow = ({option, data, expanded}: TTableRow) => {
  const [isExpand, setExpand] = useState(false)
  const [expandedData, setExpandedData] = useState<TUser[]>([])

  const clickHandler = () => {
    const arr = data.filter((item) => item.parentId === option.id)
    setExpandedData(arr)
    if (expandedData.length) setExpand(!isExpand)
  }

  return (
    <>
      <tr onClick={clickHandler} className={`${expanded && 'expanded'}`}>
        <div className='plus'>+</div>
        <td>{option.name}</td>
        <td>{option.balance}</td>
        <td>{option.email}</td>
        <td>{option.isActive ? 'Active' : 'Inactive'}</td>
      </tr>
      {isExpand && expandedData.map(dataItem => {
        return <TableRow option={dataItem} data={data} expanded={true}/>
      })}
    </>
  )
}
