import {THeader, TTableHead} from "./types";

export const TableCellHead = ({header, sort}: TTableHead) => {
  const sortHandler = (header: THeader) => {
    return () => {
      if (header.sortable && sort) {
        sort(header.value)
      }
    }
  }

  return (
    <th
      onClick={sortHandler(header)}
      className={`${header.sortable && 'cursor'}`}
    >
      {header.label}
    </th>
  )
}
