import {ButtonGroup} from "../../components/ButtonGroup/ButtonGroup";
import {FILTER_OPTIONS, TABLE_HEADERS} from "./constants";
import {Table} from "../../components/Table/Table"
import data from "../../default.json"
import {useState} from "react";
import {Button} from "../../components/Button/Button";

export const TablePage = () => {
  const [tableData, setTableData] = useState(data)
  const [activeFilter, setActiveFilter] = useState('')

  const activeFilterHandler = (value: boolean) => {
    const filteredArr = data.filter(item => item.isActive === value)
    setTableData(filteredArr)
  }

  const resetHandler = () => setTableData(data)

  return (
    <div>
      <ButtonGroup options={FILTER_OPTIONS} onClick={activeFilterHandler}/>
      <Button onClick={resetHandler}>Reset</Button>
      <Table headers={TABLE_HEADERS} data={tableData}/>
    </div>
  )
}
