import {Props, TButtonGroupOption} from "./types";

export const ButtonGroup = ({options, onClick}: Props) => {
  return (
    <div className="button-group">
      {options.map(option => {
        return (
          <button
            onClick={(e) => onClick(e.target.value)}
          >
            {option.label}
          </button>
        )
      })}
    </div>
  )
}
