import {useState} from "react";
import {Table} from "./Table";
import {TABLE_HEADERS} from "../../pages/TablePage/constants";

export const TableRow = ({option, data}: any) => {
  const [isExpand, setExpand] = useState(false)
  const [expandedData, setExpandedData] = useState([])

  const clickHandler = () => {
    const arr = data.filter((item: any) => item.parentId === option.id)
    setExpandedData(arr)
    if (expandedData.length) setExpand(!isExpand)
  }
  console.log(isExpand)
  return (
    <>
      <tr onClick={clickHandler}>
        <td>{option.name}</td>
        <td>{option.balance}</td>
        <td>{option.email}</td>
        <td>{option.isActive ? 'Active' : 'Inactive'}</td>
      </tr>
      {isExpand && (
        <Table headers={TABLE_HEADERS} data={expandedData} />
      )}
    </>
  )
}
