import {TProps} from "./types";
import {TableRow} from "./TableRow";

export const Table = ({headers, data, sort}: TProps) => {
  return (
    <table style={{width: '100%'}}>
      <thead>
        <tr>
          {headers.map(header => {
            return (
              <th onClick={() => {
                if (header.value === 'balance' || header.value === 'value') {
                  if (sort) {
                    sort(header.value)
                  }
                }
              }}
              >
                {header.label}
              </th>
            )
          })}
        </tr>
      </thead>
      <tbody>
        {data.map((dataItem: any) => {
          return <TableRow option={dataItem} data={data}/>
        })}
      </tbody>
    </table>
  )
}
