import {TTableHead} from "./types";
import {TUser} from "../../types/types";

export const TableCellHead = ({headers, sort}: TTableHead) => {
  const sortHandler = (value: keyof TUser) => {
    if (sort) {
      sort(value)
    }
  }

  return (
    <thead>
      <tr>
        {headers.map(header => {
          const isSort = header.value === 'balance' || header.value === 'email'
          return (
            <th
              onClick={() => isSort && sortHandler(header.value)}
              className={`${isSort && 'cursor'}`}
            >
              {header.label}
            </th>
          )
        })}
      </tr>
    </thead>
  )
}
