import {ButtonGroup} from "../../components/ButtonGroup/ButtonGroup";
import {FILTER_OPTIONS, TABLE_HEADERS} from "./constants";
import {Table} from "../../components/Table/Table"
import data from "../../default.json"
import {useEffect, useState} from "react";
import {Button} from "../../components/Button/Button";
import {TButtonGroupOption} from "../../components/ButtonGroup/types";
import './styles.css'
import {BUTTON_COLOR, BUTTON_TXT} from "../../components/Button/constans";
import {TUser} from "../../types/types";

const initialSortKeys = {balance: false, email: false}
const initialActiveFilter = {
  label: '',
  value: ''
}

export const TablePage = () => {
  const [tableData, setTableData] = useState<TUser[]>(data)
  const [activeFilter, setActiveFilter] = useState<TButtonGroupOption>(initialActiveFilter)
  const [sortKeys, setSortKeys] = useState<{[key: string]: boolean}>(initialSortKeys)
  const [isSorted, setIsSorted] = useState<boolean>(false)

  useEffect(() => {
    let sortedData = []
    if (activeFilter) {
      sortedData = filterHandler(activeFilter)
    }

    if (isSorted) {

    }
  },[sortKeys, activeFilter])

  const filterHandler = (option: TButtonGroupOption) => {
    return data.filter(item => item.isActive === option.status)
  }

  const resetHandler = () => {
    setActiveFilter(initialActiveFilter)
    setTableData(data)
  }

  const sortHandler = (key: string) => {
    let sortedArr = data
    if (activeFilter) {
      sortedArr = [...tableData]
    }
    if (sortKeys[key]) {
      // @ts-ignore
      sortedArr.sort((a, b) => a[key] > b[key] ? 1 : -1)
    } else {
      // @ts-ignore
      sortedArr.sort((a, b) => a[key] > b[key] ? -1 : 1 )
    }
    setSortKeys({...sortKeys, [key]: !sortKeys[key]})
    setTableData(sortedArr)
  }

  return (
    <div className="table-page">
      <ButtonGroup
        options={FILTER_OPTIONS}
        onClick={((option) => setActiveFilter(option))}
        active={activeFilter.value}
      />
      <Button
        color={BUTTON_COLOR.DANGER}
        onClick={resetHandler}
      >
        {BUTTON_TXT.RESET}
      </Button>
      <Table
        headers={TABLE_HEADERS}
        data={tableData}
        sort={sortHandler}
      />
    </div>
  )
}
