import {TTable} from "./types";
import {TableRow} from "./TableRow";

export const Table = ({headers, data, sort, main, expanded}: TTable) => {
  const expandedData = expanded || data
  return (
    <table style={{width: '100%'}}>
      {main &&
        <thead>
          <tr>
            {headers.map(header => {
              return (
                <th onClick={() => {
                  if (header.value === 'balance' || header.value === 'email') {
                    if (sort) {
                      sort(header.value)
                    }
                  }
                }}
                >
                  {header.label}
                </th>
              )
            })}
          </tr>
        </thead>
      }
      <tbody>
        {expandedData.map((dataItem) => {
          return <TableRow option={dataItem} data={data}/>
        })}
      </tbody>
    </table>
  )
}
